sources.style\\pages\\_home.scss:
html {
  scroll-behavior: smooth;
  background-color: $blanc;
}
#landing {
  height: 81vh;
  padding-top: 12vh;
  padding-left: 4.5vw;
  .side-nav {
    width: 27px;
    height: 138px;
    left: 5%;
    position: fixed;
    z-index: 9;
    top: 50%;
    transform: translateY(-50%);
    @include media("<=desktop") {
      width: 21.5px;
      height: 138px;
    }
    ul {
      width: 100%;
      height: 100%;
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      li {
        width: 100%;
        height: 27px;
        display: flex;
        align-items: center;
        justify-content: center;
        @include media("<=desktop") {
          height: 21.5px;
        }
        a {
          width: 27px;
          height: 27px;
          transform: scale(0.63);
          border-radius: 50%;
          display: block;
          background: #929292;
          transition: transform 0.3s ease-in-out;
          @include media("<=desktop") {
            height: 21.5px;
            width: 21.5px;
          }
          &.active {
            transform: scale(1);
          }
        }
      }
    }
  }
  ul.list-projets {
    display: grid;
    grid-template-columns: repeat(11, 1fr);
    @include media("<=desktop") {
      grid-template-columns: repeat(7, 1fr);
    }
    height: 130px;
    font-size: 36px;
    font-family: "Marcellus";

    p {
      width: 210px;
      text-align: center;
      position: absolute;
      opacity: 0;
      transition: 0.2s ease-in-out;
      cursor: default;
      &:nth-child(odd) {
        transform: translateX(-35%) translateY(-70%);
        &.active {
          opacity: 1;
          transform: translateX(-35%) translateY(-200%);
        }
      }
      &:nth-child(even) {
        transform: translateX(-35%) translateY(0%);
        &.active {
          opacity: 1;
          transform: translateX(-35%) translateY(90%);
        }
      }
    }
    .projet-2,
    .projet-4 {
      align-self: flex-end;
    }
    .projet-1-link,
    .projet-2-link,
    .projet-3-link,
    .projet-4-link {
      position: relative;
    }
    .projet-1-link::after {
      content: "";
      border: 1px solid $noir;
      border-radius: 50%;
      width: 55px;
      height: 55px;
      position: absolute;
      z-index: -1;
      left: 50%;
      top: 50%;
      transform: translateY(-50%) translateX(-50%);
    }
    .projet-landing {
      &::after {
        content: "";
        border: 1px solid $noir;
        border-radius: 50%;
        width: 55px;
        height: 55px;
        position: absolute;
        z-index: -1;
        left: 50%;
        top: 50%;
        transform: translateY(-50%) translateX(-50%) scale(0);
        opacity: 0;
        transition: 0.2s ease-in-out;
      }
      &:hover::after {
        transform: translateY(-50%) translateX(-50%) scale(1);
        opacity: 1;
      }
    }
  }
  h1 {
    position: absolute;
    text-align: right;
    right: 0;
    top: 50%;
    transform: translateY(-50%);
    margin-right: 11.5%;
    color: $noir;
  }
  .presentation {
    margin-top: 40vh;
    color: $noir;
  }

  .reseaux {
    margin-top: 10vh;
  }
}

#projets {
  height: 94vh;
  padding-top: 6vh;
  color: $noir;
  h2 {
    text-align: right;
    padding-bottom: 10vh;
  }
  ul.projets-num {
    display: grid;
    grid-template-columns: 1fr 4fr 1fr;
    grid-template-rows: 1fr 0.5fr 1fr 0.5fr 1fr 0.5fr 1fr;
    @include media("<=desktop") {
      grid-template-columns: 1fr 8fr 1fr;
    }
    @include media("<=tablet") {
      grid-template-columns: 0fr 1fr;
    }
    li {
      a {
        display: flex;
        span {
          font-size: 96px;
          font-family: "Marcellus";
          width: 133px;
        }
        p {
          align-self: center;
        }
      }
      &.projet-1,
      &.projet-2,
      &.projet-3,
      &.projet-4 {
        grid-column: 2/3;
      }
      &.projet-1 {
        grid-row: 1/2;
        justify-self: flex-start;
        @include media("<=tablet") {
          grid-columns: 0/1;
        }
        p {
          margin-left: 2vw;
        }
      }
      &.projet-2 {
        grid-row: 3/4;
        justify-self: flex-end;
        p {
          margin-right: 2vw;
        }
        span {
          text-align: right;
        }
      }
      &.projet-3 {
        grid-row: 5/6;
        justify-self: flex-start;
        p {
          margin-left: 2vw;
        }
      }
      &.projet-4 {
        grid-row: 7/8;
        justify-self: flex-end;
        p {
          margin-right: 2vw;
        }
        span {
          text-align: right;
        }
      }
    }
  }
}

#a-propos {
  height: 100vh;
  color: $blanc;
  .bg {
    position: absolute;
    height: 100%;
    width: 100%;
    background-color: $noir;
    left: 0;
    overflow-x: hidden;
    z-index: -1;
  }
  h2 {
    text-align: right;
    padding-top: 5vh;
  }
  h3 {
    margin-top: 7vh;
    margin-bottom: 10vh;
  }
  .a-propos-container {
    display: flex;
    justify-content: space-between;
    align-items: center;
    @include media("<=tablet") {
      flex-direction: column;
    }
    img {
      height: 300px;
      margin: auto auto;
      @include media("<=tablet") {
        height: 200px;
      }
    }
    p {
      height: 255px;
      width: 500px;
      @include media("<=tablet") {
        width: 100%;
        height: 100%;
        margin-top: 10vh;
      }
    }
  }
}

#contact {
  height: 100vh;
  color: $blanc;
  .bg {
    position: absolute;
    height: 100%;
    width: 100%;
    background-color: $noir;
    left: 0;
    overflow-x: hidden;
    z-index: -1;
  }
  h2 {
    text-align: right;
    padding-top: 5vh;
    margin-bottom: 20vh;
  }
  .mail-container {
    display: flex;

    a.mail {
      color: $blanc;
      font-family: "Marcellus";
      font-size: 36px;
      align-self: center;
      @include media("<=desktop") {
        font-size: 24px;
      }
      @include media("<=tablet") {
        font-size: 18px;
      }
      @include media("<=phone") {
        font-size: 16px;
      }
    }
    img {
      @include media("<=tablet") {
        width: 20px;
      }
      @include media("<=phone") {
        display: none;
      }
    }
  }
  p {
    margin-top: 10vh;
    width: 230px;
    margin-left: 70%;
    @include media("<=tablet") {
      width: 180px;
      margin-left: 50%;
    }
    @include media("<=phone") {
    }
  }
  .reseaux {
    margin-top: 35vh;
    color: $blanc;
  }
}

